  `site_logo` varchar(255) DEFAULT NULL,
  `site_description` text,
  `isOn` binary(1) NOT NULL DEFAULT '1',
  PRIMARY KEY (`site_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

